import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { switchMap, tap } from 'rxjs/operators';
import { VehicleInfoService } from './services/vehicle-info.service';
import { merge } from 'rxjs';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  initialVehicleId = "1"

  vehicleId = new FormControl(this.initialVehicleId)
  // initialVehicleInfo$ = this.vehicleInfoService.getVehicleInfo(this.initialVehicleId).pipe();
  vehicleInfo$ = this.vehicleId.valueChanges.pipe(
    switchMap((value) => this.vehicleInfoService.getVehicleInfo(value as string)),
    tap(console.log),
  )

  // initialVehicleInfoUrl$ = "assets/img/ranger.png"

  // initialVehicleInfoUrl = this.vehicleInfoService.getVehicleInfo(this.initialVehicleId).pipe(
  //   switchMap((value)=>this.vehicles.filter(vehicle => vehicle.id === value)),
  //   tap(console.log),
  // );

  //  vehicleInfoUrl$ = this.vehicleId.valueChanges.pipe(
  //    switchMap((value) => this.vehicleInfoService.getVehicleInfo(value as string)),
  //    tap(console.log),
  //  )


  vehicles = [{
    id: "1",
    name: "Ranger",
    url: "assets/img/ranger.png",
  }, {
    id: "2",
    name: "Mustang",
    url: "assets/img/mustang.png"
  },{
    id:"3",
    name: "Territory",
    url: "assets/img/territory.png"
  },{
    id:"4",
    name: "Bronco Sport",
    url: "assets/img/broncosport.png"
  }]

  constructor(private vehicleInfoService: VehicleInfoService) { }

  ngOnInit(): void {
  }

  getImageUrl(value){
    console.log('getImageUrl', value);
    return `assets/img/${value.vehicle_Model.toLowerCase().replace(' ', '')}.png`;
  }

  vehicleInfos$ = merge(this.initialVehicleInfo$, this.vehicleInfo$);
  // vehicleInfosUrl$ = merge(this.initialVehicleInfo$, this.vehicleInfoUrl$);

}
<div>
  <nav class="navbar navbar-light bg-white">
    <a class="navbar-brand">
    <i class="fa fa-bars mr-2"(click)="menu.abreMenu()"
    (keyup.space)="menu.abreMenu()"></i> Menu</a>
    <div>
      <a [routerLink]="['/login']"><span><img src="/assets/img/baseline_logout_black_24dp.png"/></span></a>
    </div>
  </nav>
</div>

<div class="title">
  <h3>Dashboard FORD</h3>
</div>

<div class="container" *ngIf="vehicleInfos$ | async as vehicleInfos;">

  <div class="row">

    <div class="col">
      <div class="card">
        <div class="card-header">
          Veículo
        </div>
        <div class="card-body">
          <select class="form-select"  [formControl]="vehicleId">
            <option *ngFor="let v of vehicleInfo" [ngValue]="v.id_vehic">{{v.name}}</option>
          </select>
        </div>
      </div>
    </div>

    <div class="col">
      <div class="card">
        <div class="card-header">
          Total de Vendas
        </div>
        <div class="card-body">
          <h4>{{vehicleInfos.vehicle_Sales}}</h4>
          <p class="card-text">Unid.</p>
        </div>
      </div>
    </div>

    <div class="col">
      <div class="card">
        <div class="card-header">
          Conectados
        </div>
        <div class="card-body">
          <h4>{{vehicleInfos.vehicle_Connected}}</h4>
          <p class="card-text">Unid.</p>
        </div>
      </div>
    </div>

    <div class="col">
      <div class="card">
        <div class="card-header">
          Update Software
        </div>
        <div class="card-body">
          <h4>{{vehicleInfos.vehicle_SoftwareUpdate}}</h4>
          <p class="card-text">Unid.</p>
        </div>
      </div>
    </div>

  </div>

  <div>
    <div class="image">
      <img src='{{getImageUrl(vehicleInfos)}}'/>
    </div>

  </div>
</div>

<div>
  <app-table></app-table>
</div>

<app-menu #menu>
  <img src="assets/img/ford.png" class="logo"/>
  <ul class="list-unstyled">
    <li class="line"><a [routerLink]="['/main']">Home</a></li>
    <li class="line"><a [routerLink]="['/dashboard']">Dashboard</a></li>
  </ul>
</app-menu>
