import { TabelaService } from './services/tabela.service';
import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { tap, switchMap, filter, debounceTime, distinctUntilChanged, first, map } from 'rxjs/operators';
import { merge } from 'rxjs';

const ESPERA_DIGITACAO = 300;
@Component({
  selector: 'app-tabela',
  templateUrl: './tabela.component.html',
  styleUrls: ['./tabela.component.css']
})
export class TabelaComponent {

  valorDigitado = '1';
  tabelaInput = new FormControl();
  todaTabela$ = this.tabelaService.getTabela(this.valorDigitado).pipe(
    tap(()=>{console.log("Fluxo Inicial")
  })
);

  filtroPeloInput$ = this.tabelaInput.valueChanges.pipe(
    debounceTime(ESPERA_DIGITACAO),
    tap(()=>{console.log("Fluxo do Filtro")}),
    tap(console.log),
    filter((valorDigitado)=>valorDigitado.length >= 1 || !valorDigitado.length),
    distinctUntilChanged(),
    switchMap((valorDigitado)=>this.tabelaService.getTabela(valorDigitado))
  );

  Tabela$ = merge(this.todaTabela$, this.filtroPeloInput$);

  constructor(private tabelaService: TabelaService) { }
  }


<div class="table">
  <div class="td-page-wrapper" *ngIf="Tabela$ | async as Tabela">
    <div class="po-row">
      <option class="po-md-4" *ngFor="let T of Tabela" [ngValue]="Tabela.id"></option>
    </div>
    <table>
      <thead class="table-head">
        <th>Código - Vin</th>
        <th>Odômetro</th>
        <th>Nível de Combustível</th>
        <th>Status</th>
        <th>Lat.</th>
        <th>Long.</th>
      </thead>
      <tbody class="table-body">
        <td><input [formControl]="tabelaInput"></td>
        <td>{{Tabela.odometer}}</td>
        <td>{{Tabela.fuelLevel}}</td>
        <td>{{Tabela.status}}</td>
        <td>{{Tabela.lat}}</td>
        <td>{{Tabela.long}}</td>
      </tbody>
      <tfoot class="table-foot">
      </tfoot>
    </table>
  </div>
</div>
