import { TableService } from './services/table.service';
import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { tap, switchMap, filter, debounceTime, distinctUntilChanged } from 'rxjs/operators';
import { merge } from 'rxjs';

const WAIT_TYPING = 300;
@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})
export class TableComponent {

  typedValue = '2FRHDUYS2Y63NHD22454';
  tableInput = new FormControl('2FRHDUYS2Y63NHD22454');
   allTable$ = this.tableService.getTable(this.typedValue).pipe(
     tap(()=>{console.log("Fluxo Inicial")
   })
 );

  filterByInput$ = this.tableInput.valueChanges.pipe(
    debounceTime(WAIT_TYPING),
    tap(()=>{console.log("Fluxo do Filtro")}),
    tap(console.log),
    filter((typedValue)=>typedValue.length >= 4 || !typedValue.length),
    distinctUntilChanged(),
    switchMap((typedValue)=>this.tableService.getTable(typedValue))
  );


  Table$ = merge(this.allTable$, this.filterByInput$);

  //Table$ = this.tableService.getTable(this.typedValue);


  constructor(private tableService: TableService) {


    console.log(this.Table$)
    }
    printTable(value){
      console.log(value)
    }
  }

<div class="table">
  <div class="td-page-wrapper" *ngIf="Table$ | async as Table">
    {{printTable(Table)}}
    <table>
      <thead class="table-head">
        <th>Código - Vin</th>
        <th>Odômetro</th>
        <th>Nível de Combustível</th>
        <th>Status</th>
        <th>Lat.</th>
        <th>Long.</th>
      </thead>
      <tbody class="table-body" *ngIf="Table[0]">
        <td><input [formControl]="tableInput"><button>B</button><button>U</button><button>D</button></td>
        <td><strong>{{(Table[0].odometer || '-')}} Km</strong></td>
        <td><strong>{{(Table[0].fuel_level) || '-'}} %</strong></td>
        <td><strong>{{Table[0].vehicle_status}}</strong></td>
        <td><strong>{{Table[0].latitude}}</strong></td>
        <td><strong>{{Table[0].longitude}}</strong></td>
      </tbody>
      <tfoot class="table-foot">
      </tfoot>
    </table>
  </div>
</div>
