import { TabelaService } from './services/tabela.service';
import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { tap, switchMap, filter, debounceTime, distinctUntilChanged } from 'rxjs/operators';
import { Tabela } from './tabela';
import { merge } from 'rxjs';

const ESPERA_DIGITACAO = 300;
@Component({
  selector: 'app-tabela',
  templateUrl: './tabela.component.html',
  styleUrls: ['./tabela.component.css']
})
export class TabelaComponent {
  tabelaInput = new FormControl();
  todaTabela$ = this.tabelaService.getTabela().pipe(
    tap(()=>{console.log("Fluxo Inicial")
  })
  );

  filtroPeloInput$ = this.tabelaInput.valueChanges.pipe(
    debounceTime(ESPERA_DIGITACAO),
    tap(()=>{console.log("Fluxo do Filtro")}),
    tap(console.log),
    filter((valorDigitado)=>valorDigitado.length >= 6 || !valorDigitado.length),
    distinctUntilChanged(),
    switchMap((valorDigitado)=>this.tabelaService.getTabela(valorDigitado))
  );

  Tabela$ = merge(this.todaTabela$, this.filtroPeloInput$);

  constructor(private tabelaService: TabelaService) { }
  }


<div class="table">
  <div class="td-page-wrapper" *ngIf="Tabela$ | async as Tabela; else elseBlock">
    <div class="po-row">
      <option class="po-md-4" *ngFor="let T of Tabela" [ngValue]="Tabela.vin"></option>
    </div>
  </div>
  <table>
    <thead class="table-head">
      <th>Código - Vin</th>
      <th>Odômetro</th>
        <th>Nível de Combustível</th>
        <th>Status</th>
        <th>Lat.</th>
        <th>Long.</th>
      </thead>
      <tbody class="table-body">
        <td>{{Tabela.odometer}}<input [formControl]="tabelaInput"></td>
        <td></td>
        <td></td>
        <td></td>
        <td></td>
        <td></td>
      </tbody>
      <tfoot class="table-foot">
      </tfoot>
    </table>
    <ng-template #elseBlock><p>Não há dados</p></ng-template>
  </div>
